package com.example.apptiendaeval2

import com.example.apptiendaval2.viewmodel.CartItem
import com.example.apptiendaeval2.model.Producto
import org.junit.Test
import org.junit.Assert.*

/**
 * Test unitarios para CartItem
 * Valida la estructura y comportamiento del data class CartItem
 */
class CartItemTest {

    private val productoTest = Producto(
        id = 1L,
        nombre = "Camiseta Test",
        precio = 15000,
        descripcion = "Camiseta de prueba"
    )

    @Test
    fun `test crear CartItem con valores por defecto`() {
        val cartItem = CartItem(producto = productoTest)

        assertEquals(productoTest, cartItem.producto)
        assertEquals(1, cartItem.cantidad)
        assertEquals("S", cartItem.talla)
    }

    @Test
    fun `test crear CartItem con cantidad personalizada`() {
        val cartItem = CartItem(
            producto = productoTest,
            cantidad = 5,
            talla = "L"
        )

        assertEquals(5, cartItem.cantidad)
        assertEquals("L", cartItem.talla)
    }

    @Test
    fun `test copy de CartItem aumentando cantidad`() {
        val cartItemOriginal = CartItem(producto = productoTest, cantidad = 1)
        val cartItemModificado = cartItemOriginal.copy(cantidad = 3)

        assertEquals(1, cartItemOriginal.cantidad)
        assertEquals(3, cartItemModificado.cantidad)
    }

    @Test
    fun `test copy de CartItem cambiando talla`() {
        val cartItemOriginal = CartItem(producto = productoTest, talla = "S")
        val cartItemModificado = cartItemOriginal.copy(talla = "XL")

        assertEquals("S", cartItemOriginal.talla)
        assertEquals("XL", cartItemModificado.talla)
    }

    @Test
    fun `test igualdad de CartItems con mismo producto y talla`() {
        val cartItem1 = CartItem(producto = productoTest, cantidad = 2, talla = "M")
        val cartItem2 = CartItem(producto = productoTest, cantidad = 2, talla = "M")

        assertEquals(cartItem1, cartItem2)
    }

    @Test
    fun `test calcular subtotal de CartItem`() {
        val cartItem = CartItem(
            producto = productoTest.copy(precio = 10000),
            cantidad = 3
        )

        val subtotal = (cartItem.producto.precio ?: 0) * cartItem.cantidad
        assertEquals(30000, subtotal)
    }
}

package com.example.apptiendaeval2

import com.example.apptiendaeval2.model.Producto
import com.example.apptiendaeval2.model.Categoria
import com.example.apptiendaval2.viewmodel.CartViewModel
import org.junit.Before
import org.junit.Test
import org.junit.Assert.*

/**
 * Test unitarios para CartViewModel
 * Valida operaciones CRUD del carrito de compras
 */
class CartViewModelTest {

    private lateinit var viewModel: CartViewModel
    private lateinit var productoTest: Producto

    @Before
    fun setup() {
        viewModel = CartViewModel()
        productoTest = Producto(
            id = 1L,
            nombre = "Camiseta Dragon",
            precio = 15990,
            descripcion = "Camiseta con diseño de dragón",
            imagenUrl = "https://example.com/imagen.jpg",
            categoria = Categoria.POLERAS
        )
    }

    @Test
    fun `test agregar producto al carrito`() {
        viewModel.addProduct(productoTest, "M")

        val items = viewModel.items.value
        assertEquals("El carrito debe tener 1 item", 1, items.size)
        assertEquals("El producto debe ser el correcto", productoTest.id, items[0].producto.id)
        assertEquals("La talla debe ser M", "M", items[0].talla)
    }

    @Test
    fun `test agregar mismo producto aumenta cantidad`() {
        viewModel.addProduct(productoTest, "M")
        viewModel.addProduct(productoTest, "M")

        val items = viewModel.items.value
        assertEquals("El carrito debe tener 1 item", 1, items.size)
        assertEquals("La cantidad debe ser 2", 2, items[0].cantidad)
    }

    @Test
    fun `test agregar mismo producto con diferentes tallas`() {
        viewModel.addProduct(productoTest, "M")
        viewModel.addProduct(productoTest, "L")

        val items = viewModel.items.value
        assertEquals("El carrito debe tener 2 items", 2, items.size)
    }

    @Test
    fun `test remover producto del carrito`() {
        viewModel.addProduct(productoTest, "M")
        viewModel.removeProduct(productoTest.id, "M")

        val items = viewModel.items.value
        assertEquals("El carrito debe estar vacío", 0, items.size)
    }

    @Test
    fun `test actualizar cantidad de producto`() {
        viewModel.addProduct(productoTest, "M")
        viewModel.updateQuantity(productoTest.id, "M", 5)

        val items = viewModel.items.value
        assertEquals("La cantidad debe ser 5", 5, items[0].cantidad)
    }

    @Test
    fun `test remover un item cuando cantidad es mayor a 1`() {
        viewModel.addProduct(productoTest, "M")
        viewModel.addProduct(productoTest, "M")
        viewModel.removeOneItem(productoTest.id, "M")

        val items = viewModel.items.value
        assertEquals("El carrito debe tener 1 item", 1, items.size)
        assertEquals("La cantidad debe ser 1", 1, items[0].cantidad)
    }

    @Test
    fun `test limpiar carrito`() {
        viewModel.addProduct(productoTest, "M")
        viewModel.setPaymentMethod("Tarjeta")
        viewModel.clear()

        val items = viewModel.items.value
        val paymentMethod = viewModel.paymentMethod.value

        assertEquals("El carrito debe estar vacío", 0, items.size)
        assertEquals("El método de pago debe estar limpio", "", paymentMethod)
    }

    @Test
    fun `test setShippingData guarda los datos correctamente`() {
        viewModel.setShippingData(
            nombre = "Juan Pérez",
            direccion = "Av. Principal 123",
            ciudad = "Santiago",
            comuna = "Las Condes",
            telefono = "912345678"
        )

        val shippingData = viewModel.shippingData.value
        assertEquals("Juan Pérez", shippingData["nombre"])
        assertEquals("Av. Principal 123", shippingData["direccion"])
        assertEquals("Santiago", shippingData["ciudad"])
        assertEquals("Las Condes", shippingData["comuna"])
        assertEquals("912345678", shippingData["telefono"])
    }

    @Test
    fun `test setPaymentMethod guarda el metodo de pago`() {
        viewModel.setPaymentMethod("Tarjeta de Crédito")
        assertEquals("Tarjeta de Crédito", viewModel.paymentMethod.value)
    }

    @Test
    fun `test setPaymentAmount guarda el monto`() {
        viewModel.setPaymentAmount(25000)
        assertEquals(25000, viewModel.paymentAmount.value)
    }

    @Test
    fun `test selectProduct guarda el producto seleccionado`() {
        viewModel.selectProduct(productoTest)
        assertEquals(productoTest, viewModel.selectedProduct.value)
    }
}

package com.example.apptiendaeval2

import com.example.apptiendaeval2.model.Categoria
import org.junit.Test
import org.junit.Assert.*

/**
 * Test unitarios para el enum Categoria
 * Valida la estructura y comportamiento del enum Categoria
 */
class CategoriaTest {

    @Test
    fun `test categoria POLERAS tiene displayName correcto`() {
        assertEquals("Poleras", Categoria.POLERAS.displayName)
    }

    @Test
    fun `test categoria PANTALONES tiene displayName correcto`() {
        assertEquals("Pantalones", Categoria.PANTALONES.displayName)
    }

    @Test
    fun `test categoria POLERONES tiene displayName correcto`() {
        assertEquals("Polerones", Categoria.POLERONES.displayName)
    }

    @Test
    fun `test obtener categoria por nombre`() {
        val categoria = Categoria.valueOf("POLERAS")
        assertEquals(Categoria.POLERAS, categoria)
    }

    @Test
    fun `test todas las categorias estan presentes`() {
        val categorias = Categoria.values()
        assertEquals(3, categorias.size)
        assertTrue(categorias.contains(Categoria.POLERAS))
        assertTrue(categorias.contains(Categoria.PANTALONES))
        assertTrue(categorias.contains(Categoria.POLERONES))
    }

    @Test
    fun `test toString de categoria devuelve nombre`() {
        val categoriaString = Categoria.POLERAS.toString()
        assertEquals("POLERAS", categoriaString)
    }

    @Test
    fun `test comparacion de categorias`() {
        val categoria1 = Categoria.POLERAS
        val categoria2 = Categoria.POLERAS
        val categoria3 = Categoria.PANTALONES

        assertEquals(categoria1, categoria2)
        assertNotEquals(categoria1, categoria3)
    }
}

package com.example.apptiendaeval2

import com.example.apptiendaeval2.utils.ChileData
import org.junit.Test
import org.junit.Assert.*

/**
 * Test unitarios para ChileData
 * Valida las listas de comunas y ciudades de Chile
 */
class ChileDataTest {

    @Test
    fun `test comunas de Santiago no esta vacia`() {
        assertTrue("La lista de comunas debe tener elementos", ChileData.comunasSantiago.isNotEmpty())
    }

    @Test
    fun `test comunas de Santiago contiene Las Condes`() {
        assertTrue(
            "Las Condes debe estar en la lista",
            ChileData.comunasSantiago.contains("Las Condes")
        )
    }

    @Test
    fun `test comunas de Santiago contiene Providencia`() {
        assertTrue(
            "Providencia debe estar en la lista",
            ChileData.comunasSantiago.contains("Providencia")
        )
    }

    @Test
    fun `test comunas de Santiago contiene Maipu`() {
        assertTrue(
            "Maipú debe estar en la lista",
            ChileData.comunasSantiago.contains("Maipú")
        )
    }

    @Test
    fun `test comunas de Santiago esta ordenada alfabeticamente`() {
        val comunasOrdenadas = ChileData.comunasSantiago.sorted()
        assertEquals(
            "Las comunas deben estar ordenadas alfabéticamente",
            comunasOrdenadas,
            ChileData.comunasSantiago
        )
    }

    @Test
    fun `test comunas de Santiago no tiene duplicados`() {
        val comunasUnicas = ChileData.comunasSantiago.distinct()
        assertEquals(
            "No debe haber comunas duplicadas",
            comunasUnicas.size,
            ChileData.comunasSantiago.size
        )
    }

    @Test
    fun `test todas las comunas son strings no vacios`() {
        ChileData.comunasSantiago.forEach { comuna ->
            assertTrue("Cada comuna debe tener texto", comuna.isNotBlank())
        }
    }

    @Test
    fun `test cantidad minima de comunas`() {
        assertTrue(
            "Debe haber al menos 30 comunas en Santiago",
            ChileData.comunasSantiago.size >= 30
        )
    }
}

package com.example.apptiendaeval2

import com.example.apptiendaeval2.model.Producto
import com.example.apptiendaeval2.model.Categoria
import com.example.apptiendaeval2.model.Valoracion
import org.junit.Test
import org.junit.Assert.*

/**
 * Test unitarios para el modelo Producto
 * Valida la estructura y comportamiento del data class Producto
 */
class ProductoTest {

    @Test
    fun `test crear producto con todos los campos`() {
        val producto = Producto(
            id = 1L,
            nombre = "Camiseta Test",
            precio = 10000,
            descripcion = "Descripción de prueba",
            imagenUrl = "https://example.com/imagen.jpg",
            categoria = Categoria.POLERAS,
            imagenesUrl = listOf("url1", "url2"),
            tallas = listOf("S", "M", "L"),
            medidas = listOf("Ancho: 50cm", "Largo: 70cm")
        )

        assertEquals(1L, producto.id)
        assertEquals("Camiseta Test", producto.nombre)
        assertEquals(10000, producto.precio)
        assertEquals("Descripción de prueba", producto.descripcion)
        assertEquals("https://example.com/imagen.jpg", producto.imagenUrl)
        assertEquals(3, producto.tallas.size)
    }

    @Test
    fun `test producto con valores por defecto`() {
        val producto = Producto()

        assertNull(producto.id)
        assertNull(producto.nombre)
        assertNull(producto.precio)
        assertEquals(emptyList<String>(), producto.imagenesUrl)
        assertEquals(emptyList<Valoracion>(), producto.valoraciones)
    }

    @Test
    fun `test producto con tallas por defecto`() {
        val producto = Producto(
            id = 1L,
            nombre = "Camiseta"
        )

        assertEquals(listOf("S", "M", "L", "XL"), producto.tallas)
    }

    @Test
    fun `test copy de producto cambiando precio`() {
        val productoOriginal = Producto(
            id = 1L,
            nombre = "Camiseta",
            precio = 10000
        )

        val productoModificado = productoOriginal.copy(precio = 15000)

        assertEquals(10000, productoOriginal.precio)
        assertEquals(15000, productoModificado.precio)
        assertEquals(productoOriginal.nombre, productoModificado.nombre)
    }

    @Test
    fun `test igualdad de productos con mismo ID`() {
        val producto1 = Producto(id = 1L, nombre = "Camiseta A")
        val producto2 = Producto(id = 1L, nombre = "Camiseta A")

        assertEquals(producto1, producto2)
    }

    @Test
    fun `test productos diferentes con distinto ID`() {
        val producto1 = Producto(id = 1L, nombre = "Camiseta")
        val producto2 = Producto(id = 2L, nombre = "Camiseta")

        assertNotEquals(producto1, producto2)
    }
}

